<template>
    <div>
        <div class="tableFuncs">
            <Button-group shape="circle">
                <Button type="ghost" icon="ios-undo" @click="goBack">
                    <span>返回</span>
                </Button>
            </Button-group>
        </div>
        <Affix style="float:left">
            <Menu :active-name="active" @on-select="menuSelect" style="width: 180px;">
                <MenuGroup :title="'楼栋:' + editInfor.name">
                    <MenuItem name="info">
                    <Icon type="document-text"></Icon>
                    基本信息
                    </MenuItem>
                    <MenuItem name="source">
                    <Icon type="android-compass"></Icon>
                    房源列表
                    </MenuItem>
                    <MenuItem name="lock">
                    <Icon type="clock"></Icon>
                    门锁信息
                    </MenuItem>
                    <MenuItem name="members">
                    <Icon type="person-stalker"></Icon>
                    入住人员
                    </MenuItem>
                </MenuGroup>
            </Menu>
        </Affix>
        <div style="margin-left: 200px;" v-if="active == 'info'">
            <Form ref="editForm" :model="formData" :rules="ruleValidate" label-position="right" :label-width="100">
                <Row :gutter="16">
                    <Col span="12" offset="6">
                    <div class="form-title">
                        <Icon type="document-text"></Icon>基础信息:
                    </div>
                    <Form-item label="项目名称" prop="name">
                        <Input v-model="formData.name" placeholder="请输入"></Input>
                    </Form-item>
                    <Form-item label="项目编号" prop="number">
                        <Input v-model="formData.number" placeholder="请输入"></Input>
                    </Form-item>
                    <Form-item label="配套" prop="peitao">
                        <Select v-model="formData.peitao" multiple placeholder="请选择">
                            <Option :value="1">冰箱</Option>
                            <Option :value="2">洗衣机</Option>
                            <Option :value="3">台灯</Option>
                            <Option :value="4">空调</Option>
                        </Select>
                    </Form-item>
                    <Form-item label="项目介绍" prop="intro">
                        <Input v-model="formData.intro" type="textarea" placeholder="请输入" :rows="10"></Input>
                    </Form-item>
                    <Form-item label="项目图片" class="avatar-list">
                        <AvaterView placement="left" uri="data:image/jpeg;base64,"></AvaterView>
                        <div class="clear"></div>
                        <VueCoreImageUpload></VueCoreImageUpload>
                    </Form-item>
                    <div class="form-title">
                        <Icon type="leaf"></Icon>物业信息:
                    </div>
                    <Form-item label="物业公司" prop="wy_org">
                        <Input v-model="formData.wy_org" placeholder="请输入"></Input>
                    </Form-item>
                    <Form-item label="物业负责人" prop="wy_charge">
                        <Input v-model="formData.wy_charge" placeholder="请输入"></Input>
                    </Form-item>
                    <Form-item label="物业联系方式" prop="wy_concat">
                        <Input v-model="formData.wy_contact" placeholder="请输入"></Input>
                    </Form-item>
                    <div class="form-title">
                        <Icon type="android-compass"></Icon>项目位置:
                    </div>
                    <Form-item label="项目地址" prop="address">
                        <Input v-model="formData.address" placeholder="请输入"></Input>
                    </Form-item>
                    <Form-item label="地理坐标" prop="location">
                        <Input v-model="formData.location" placeholder="经纬度请用逗号分隔"></Input>
                        <div class="intro-big-pic" style="margin-top:10px;">
                            <img style="width: 100%; height: 200px;" src="data:image/jpeg;base64,">
                        </div>
                    </Form-item>
                    <Form-item label="">
                        <Button size="large" type="primary" icon="checkmark-circled" @click="handleSubmit('editForm')">提交</Button>
                        <Button size="large" type="ghost" icon="refresh" @click="handleReset('editForm')" style="margin-left: 8px">重置</Button>
                    </Form-item>
                    </Col>
                </Row>
            </Form>
        </div>
        <div style="margin-left: 200px;" v-if="active == 'location'">
            <div span="12" v-for="item in locations">
                <Card style="margin-bottom: 15px;">
                    <p slot="title">
                        <Icon type="android-time"></Icon>
                        入住: {{item.time}}
                    </p>
                    <span slot="extra">
                      <Icon type="location"></Icon>
                      {{item.status_msg}}
                  </span>
                    <span v-if="item.status == 0">{{item.location_msg}}</span>
                    <MchooseLocation style=" flaot: left;" v-if="item.status == 1" v-model="item.location" :max="4"></MchooseLocation>
                    <Button v-if="item.status == 1" type="primary" @click="locationEdit(item.location)">保存</Button>
                </Card>
            </div>
        </div>
        <div style="margin-left: 200px;" v-if="active == 'members'">
            <TableScoller>
                <Table ref="tableA" :columns="columnsA" @on-row-dblclick="goEdit" :data="tableMembers" stripe border></Table>
            </TableScoller>
        </div>
        <div style="margin-left: 200px;" v-if="active == 'lock'">
            <TableScoller>
                <Table ref="tableB" :columns="columnsB" :data="tableLock" stripe border></Table>
            </TableScoller>
        </div>
        <div style="margin-left: 200px;" v-if="active == 'source'">
            <TableScoller>
                <Table ref="tableC" :columns="columnsC" @on-row-dblclick="goSourceEdit" :data="tableFloor" stripe border></Table>
            </TableScoller>
        </div>
    </div>
</template>
<script>
export default {
    data() {
        return {
            active: '0',
            dateValue: '', // 日历控件
            editInfor: {}, // 表单详情
            formData: {
                name: '',
                number: '',
                photos: '',
                intro: '',
                peitao: [],
                wy_org: '',
                wy_charge: '',
                wy_contact: '',
                address: '',
                location: ''
            },
            ruleValidate: {
                name: [{
                    required: true,
                    message: '请输入项目名称'
                }],
                number: [{
                    required: true,
                    message: '请输入项目编号'
                }],
                address: [{
                    required: true,
                    message: '请输入项目地址'
                }]
            },
            locations: [],
            tableFloor: [],
            tableMembers: [],
            tableLock: [],
            columnsA: [{
                title: '姓名',
                key: 'name',
                className: 'avatarImg',
                width: 100
            }, {
                title: '房号',
                key: 'house_number',
                align: 'center',
                className: 'avatarImg',
                width: 100
            }, {
                title: '性别',
                key: 'gender',
                align: 'center',
                width: 80
            }, {
                title: '出生日期',
                key: 'birth',
                width: 110,
                align: 'center'
            }, {
                title: '身份证号',
                key: 'card_id',
                width: 180,
                align: 'center'
            }, {
                title: '工作单位',
                key: 'org',
            }, {
                title: '手机号码',
                key: 'phone',
                width: 120,
            }, {
                title: '门锁权限',
                key: 'door_status',
                align: 'center'
            }, {
                title: '操作',
                key: 'address',
                width: 100,
                align: 'center',
                render: (h, params) => {
                    const row = params.row;
                    const isSuper = row.super == 1;
                    return this.$lodash.renderButtonGroup(h, params, [{
                        icon: 'edit',
                        title: '编辑',
                        click: () => {
                            this.goEdit(params.row);
                        }
                    }])
                }
            }],
            columnsB: [{
                title: '门锁编号', // 门锁序号, 房源编号, 房源名称, 通讯盒序号, 绑定时间, 撤换时间, 有效日期
                key: 'id'
            }, {
                title: '通讯盒序号',
                key: 'box_number',
                align: 'center',
                className: 'avatarImg',
                width: 130
            }, {
                title: '品牌/厂家',
                key: 'company',
                align: 'center',
                width: 120
            }, {
                title: '有效期',
                key: 'avalid_date',
                align: 'center'
            }, {
                title: '开关状态',
                key: 'open_status',
                width: 120,
                align: 'center',
                render: (h, params) => {
                    const row = params.row;
                    const isopen = row.open_status == 1;
                    return h('Tag', {
                        props: {
                            type: 'border',
                            color: isopen ? 'green' : 'red'
                        }
                    }, isopen ? '开' : '关')
                }
            }, {
                title: '门锁状态',
                key: 'status',
                width: 90,
                align: 'center',
                render: (h, params) => {
                    const row = params.row;
                    const isok = row.status == 1;
                    return h('Tag', {
                        props: {
                            type: 'border',
                            color: isok ? 'green' : 'red'
                        }
                    }, isok ? '正常' : '损坏')
                }
            }, {
                title: '电量',
                key: 'power',
                width: 120,
                align: 'center',
                render: (h, params) => {
                    const row = params.row;
                    return h('i-circle', {
                        style: {
                            'marginTop': '5px'
                        },
                        props: {
                            percent: row.power,
                            'stroke-width': 8,
                            'stroke-color': '#5cb85c',
                            size: 36
                        }
                    }, row.power)
                }
            }, {
                title: '上次开门',
                key: 'last_open',
                align: 'center',
                width: 160,
            }, {
                title: '操作',
                key: 'address',
                width: 120,
                align: 'center',
                render: (h, params) => {
                    const row = params.row;
                    const isSuper = row.super == 1;
                    return h('ButtonGroup', [
                        // h('Button', {
                        //     props: {
                        //         disabled: isSuper,
                        //         type: 'ghost',
                        //         icon: 'close',
                        //         size: 'small'
                        //     },
                        //     on: {
                        //         click: () => {
                        //             this.selectedGroup = [row];
                        //             this.showDel();
                        //         }
                        //     }
                        // }),
                        h('Button', {
                            props: {
                                type: 'ghost',
                                icon: 'edit',
                                size: 'small'
                            },
                            on: {
                                click: () => {
                                    this.editLock(row);
                                }
                            }
                        })
                    ]);
                }
            }],
            columnsC: [{
                title: '项目',
                key: 'project',
                width: 140
            }, {
                title: '楼栋',
                key: 'floor',
                align: 'center',
                className: 'avatarImg',
                width: 130
            }, {
                title: '房号',
                key: 'house_number',
                align: 'center',
                width: 80
            }, {
                title: '面积',
                key: 'area',
                width: 110,
                align: 'center'
            }, {
                title: '户型',
                key: 'house_type',
                width: 120,
                align: 'center'
            }, {
                title: '入住人数',
                key: 'count',
                width: 90,
                align: 'center'
            }, {
                title: '租期',
                key: 'rent_date',
                align: 'center',
                width: 120,
            }, {
                title: '门锁编号',
                key: 'door_number',
                align: 'center'
            }, {
                title: '通信盒编号',
                key: 'door_box_number',
                align: 'center'
            }, {
                title: '房租',
                key: 'rent_pirce',
                align: 'center'
            }, {
                title: '操作',
                key: 'address',
                width: 100,
                align: 'center',
                render: (h, params) => {
                    const row = params.row;
                    const isSuper = row.super == 1;
                    return h('ButtonGroup', [
                        h('Button', {
                            props: {
                                disabled: isSuper,
                                type: 'ghost',
                                icon: 'close',
                                size: 'small'
                            },
                            on: {
                                click: () => {
                                    this.activeParams = params;
                                    this.delSource();
                                }
                            }
                        }),
                        h('Button', {
                            props: {
                                type: 'ghost',
                                icon: 'edit',
                                size: 'small'
                            },
                            on: {
                                click: () => {
                                    this.goEdit(params.row.id);
                                }
                            }
                        })
                    ]);
                }
            }]
        }
    },
    computed: {
        id() {
            return this.$route.params.id;
        },
        paramsFilter() { // 表单提交字段
            return ['id', 'name', 'relation', 'resource', 'gender', 'card_id', 'sugestion', 'birth', 'phone', 'org'];
        }
    },
    watch: {
        active(name) {
            if (name == 'info') {
                this.getInfo();
            } else if (name == 'members') {
                this.getMembers();
            } else if (name == 'lock') {
                this.getLock();
            } else if (name == 'source') {
                this.getSource();
            }
        },
    },
    created() {
        this.active = 'info';
        this.$store.commit('breadcrumb', [{
            name: '房源管理',
            href: '/resource'
        }]);
    },
    methods: {
        goEdit(row) {
            this.active == 'info';
            setTimeout(() => {
                this.active = 'info';
            })
            this.$router.push({
                'name': 'members.edit',
                'params': {
                    id: row.id
                }
            });
        },
        goSourceEdit(row){
            this.$router.push({
                'name': 'resource.edit',
                'params': {
                    id: row.id
                }
            });
        },
        goFloorEdit(row) {
            this.$router.push({
                'name': 'floor.edit',
                'params': {
                    id: row.id
                }
            });
        },
        menuSelect(name) {
            this.active = name;
        },
        getInfo() {
            this.$lodash.api(this, 'floorInfo', {
                id: this.id
            }).then(res => {
                this.editInfor = res.data;
                this.$lodash.assign(this.formData, this.editInfor);
            })
        },
        getMembers() {
            this.$lodash.api(this, 'floorMembers', {
                id: this.id
            }).then(res => {
                this.tableMembers = res.data.data || [];
            })
        },
        getLock() {
            this.$lodash.api(this, 'floorLocks', {
                uid: this.uid
            }).then(res => {
                this.tableLock = res.data.data || [];
            })
        },
        getSource() {
            this.$lodash.api(this, 'floorSource', {
                uid: this.uid
            }).then(res => {
                this.tableFloor = res.data.data || [];
            })
        },
        locationEdit(location) {
            this.$lodash.api(this, 'membersLocationEdit', {
                uid: this.uid,
                location
            }).then(res => {
                this.$Message.success('编辑成功');
            })
        },
        // 返回上一页
        goBack() {
            this.$router.go(-1);
        },
        // 提交成功后返回
        submitBack() {
            this.$router.push({
                name: 'resource.index'
            });
        },
        // 图片上传成功
        imageUploaded(url) {
            this.formData.avatar = url;
        },
        // 清除日历控件
        dateClear() {
            this.formData.birth = '';
        },
        // 日历改变
        dateChange(dateRange) {
            const curDateRange = this.$refs.date.formattingDate(this.dateValue);
            this.formData.birth = curDateRange;
        },
        // 提交表单
        handleSubmit(name) {
            // 验证通过
            this.$refs[name].validate((valid) => {
                if (valid) {
                    this.$lodash.api(this, 'projectEdit', this.formData).then(res => {
                        this.submitBack();
                        this.$Message.success('编辑成功');
                    });
                }
            })
        },
        // 重置表单
        handleReset(name) {
            this.$refs.date.handleClear();
            this.$refs[name].resetFields();
            this.$lodash.assign(this.formData, this.editInfor);
        },
        // 单个Table删除表格
        delSource() {
            const row = this.activeParams.row;
            this.$Modal.confirm({
                title: '确认删除',
                content: row.project + '-' + row.project + '-' + row.house_number,
                loading: true,
                onOk: () => {
                    this.$lodash.api(this, 'resourceDelete', {
                        id: row.id
                    }).then(res => {
                        this.$Modal.remove();
                        this.getSource();
                    }, 1000)
                }
            })
        }
    }
}
</script>
<style scoped>
.ivu-menu-item-group-title {
    display: none;
}
</style>