<template>
  <div>
    <div class="tableFuncs">
      <Button-group shape="circle">
        <Button type="ghost" icon="ios-undo" @click="goBack">
          <span>返回</span>
        </Button>
      </Button-group>
    </div>
    <Form ref="editForm" :model="formData" :rules="ruleValidate" label-position="right" :label-width="90">
      <Row :gutter="16">
        <Col span="12">
        <Form-item label="楼栋名称">
          <Input v-model="formData.name" placeholder="请输入"></Input>
        </Form-item>
        <Form-item label="选择房源">
          <Select v-model="formData.resource" placeholder="请选择">
            <Option value="beijing">半山家园</Option>
            <Option value="shanghai">青年公寓</Option>
            <Option value="shenzhen">四海新城</Option>
          </Select>
        </Form-item>
        <Form-item label="楼栋介绍" prop="email">
          <Input v-model="formData.name" type="textarea" placeholder="请输入" :rows="7"></Input>
        </Form-item>
        </Col>
        <Col span="12">
        <Form-item label="物业公司" prop="email">
          <Input v-model="formData.org" placeholder="请输入"></Input>
        </Form-item>
        <Form-item label="物业负责人" prop="email">
          <Input v-model="formData.org" placeholder="请输入"></Input>
        </Form-item>
        <Form-item label="物业联系方式" prop="email">
          <Input v-model="formData.org" placeholder="请输入"></Input>
        </Form-item>
        <Form-item label="上传图片" class="avatar-list">
          <AvaterView placement="left" uri="data:image/jpeg;base64,"></AvaterView>
          <AvaterView placement="left" uri="data:image/jpeg;base64,"></AvaterView>
          <div class="clear"></div>
          <VueCoreImageUpload></VueCoreImageUpload>
        </Form-item>
        </Col>
      </Row>
      <Row style="text-align: center; padding: 20px">
        <Button size="large" :type="isEdit ? 'error' : 'primary'" icon="checkmark-circled" @click="handleSubmit()">提交</Button>
        <Button size="large" type="ghost" icon="refresh" @click="handleReset()" style="margin-left: 8px">重置</Button>
      </Row>
    </Form>
  </div>
</template>
<script>
export default {
  data() {
    return {
      adminUser: '',
      editInfor: null,
      toggleStatus: true,
      isSuper: false,
      formData: {
        name: '',
        relation: '',
        floor: '',
        resource: '',
        gender: 'male',
        card_id: '',
        card_id_number: '',
        sugestion: '',
        status: '',
        birth: '',
        phone: '',
        org: ''
      },
      ruleValidate: {
        name: [{
          required: true,
          message: '请输入姓名'
        }],
        resource: [{
          required: true,
          message: '请选择房源'
        }],
        floor: [{
          required: true,
          message: '请选择楼栋'
        }],
        relation: [{
          required: true,
          message: '请选择与申请人关系'
        }],
        card_id: [{
          required: true,
          message: '请填写身份证号码'
        }],
        card_id_number: [{
          required: true,
          message: '请填写身份证物理ID'
        }],
        phone: [{
          required: true,
          message: '请填写手机号码'
        }, {
          required: true,
          validator: this.$rules.phone
        }]
      },
      treeData: []
    }
  },
  computed: {
    isEdit() {
      return this.$route.name == 'members.edit';
    },
    adminId() {
      return this.$route.params.adminId;
    }
  },
  mounted() {
    this.$apis.adminInfo({
      id: this.adminId || 0
    }).then(res => {
      let result = res.data;

      if (this.isEdit) {
        this.adminUser = result.user_info.name + '(' + result.user_info.email + ')';
        this.isSuper = result.user_info.super == 1; // 是否为超级管理员
        this.toggleStatus = result.user_info.status == 1;
      } else {
        this.isSuper = false;
        this.toggleStatus = true;
      }

      this.$lodash.forEach(result.tree, (parent) => {
        parent.expand = true;
        if (this.isSuper) {
          parent.disableCheckbox = true;
          this.$lodash.forEach(parent.children, (item) => {
            item.disableCheckbox = true;
          });
        }
      });

      this.editInfor = result;
      this.$lodash.assignIn(this.formData, this.editInfor);
    });
  },
  methods: {
    goBack() {
      this.$router.go(-1);
    },
    imageuploaded(res) {
      this.formData.new_avatar = res.data.url;
    },
    changeStatus() {
      this.formData.status = this.toggleStatus ? 10 : 20;
    },
    editorChange(contents) {
      this.formData.contents = contents;
    },
    handleSubmit() {
      this.$refs['editForm'].validate((valid) => {
        if (valid) {
          this.$apis.adminEdit({
            id: this.adminId || 0,
            email: this.formData.email,
            status: this.toggleStatus ? 1 : 0,
            menus: ids.join(',')
          }).then(res => {
            this.$Message.success('保存成功!');
            if (this.isEdit) {
              this.goBack();
            } else {
              this.$router.push({
                name: 'system.admin'
              });
            }
          });
        }
      })
    },
    handleReset() {
      this.formData.birth = '';
      this.$refs['editForm'].resetFields();
    }
  }
}
</script>
