<script>
export default {
  data() {
    return {
      projectId: 2,
      searchVisible: false,
      isEditInfo: false,
      isProject: true,
      formData: {},
      tabActive: 'source',
      activeName: 1,
      toggleStatus: 1,
      activeParams: null, // 当前操作行数据
      selection: [], // 全选数组
      selectedGroup: [], // 多选中的行
      selectedGroupNames: [],
      modelDel: false, // 删除model显示隐藏
      modalLoading: false, // 加载按钮加载中提示
      chooseParams: {},
      queryParams: { // 表头筛选
        pid: '',
        type: '',
        birth: '',
        huxing: '',
        keyword: ''
      },
      tableData: [],
      cardData: [],
      modelFamily: false,
      familyData: [],
      page: {
        total: 15,
        pagesize: 1
      },
      columns: [{
        type: 'selection',
        width: 60,
        align: 'center'
      }, {
        title: '项目',
        key: 'project',
        width: 140
      }, {
        title: '楼栋',
        key: 'floor',
        align: 'center',
        className: 'avatarImg',
        width: 130
      }, {
        title: '房号',
        key: 'house_number',
        align: 'center',
        width: 80
      }, {
        title: '面积',
        key: 'area',
        width: 110,
        align: 'center'
      }, {
        title: '户型',
        key: 'house_type',
        width: 120,
        align: 'center'
      }, {
        title: '入住人数',
        key: 'count',
        width: 90,
        align: 'center'
      }, {
        title: '租期',
        key: 'rent_date',
        align: 'center',
        width: 120,
      }, {
        title: '门锁编号',
        key: 'door_number',
        align: 'center'
      }, {
        title: '通信盒编号',
        key: 'door_box_number',
        align: 'center'
      }, {
        title: '房租',
        key: 'rent_pirce',
        align: 'center'
      }, {
        title: '操作',
        key: 'address',
        width: 100,
        align: 'center',
        render: (h, params) => {
          const row = params.row;
          const isSuper = row.super == 1;
          return h('ButtonGroup', [
            h('Button', {
              props: {
                disabled: isSuper,
                type: 'ghost',
                icon: 'close',
                size: 'small'
              },
              on: {
                click: () => {
                  this.selectedGroup = [row];
                  this.showDel();
                }
              }
            }),
            h('Button', {
              props: {
                type: 'ghost',
                icon: 'edit',
                size: 'small'
              },
              on: {
                click: () => {
                  this.goEdit(params.row.id);
                }
              }
            })
          ]);
        }
      }]
    }
  },
  computed: {
    pageSize() {
      return this.$store.getters.pageSize;
    }
  },
  created() {
    const curPage = this.$route.query.page || 1;
    this.cardData = this.$lodash.testData({
      house_number: 'B栋411',
      family: [{
        'name': '张晓玲',
        'isManager': true,
      }, {
        'name': '张晓明',
        'isManager': false,
      }, {
        'name': '大花',
        'isManager': false,
      }],
      family_count: 3
    });
    this.getData(curPage);
  },
  watch: {
    tabActive() {
      this.isEditInfo = false;
    }
  },
  methods: {
    changeMenus(parms) {
      this.chooseParams = parms;
      this.tabActive = 'source';
      this.getData(1);
    },
    toggleSearch() {
      this.searchVisible = !this.searchVisible;
    },
    goAdd(name) {
      this.$router.push({
        'name': name
      });
    },
    goEdit(id) {
      this.$router.push({
        'name': 'project.edit',
        'params': {
          id
        }
      });
    },
    goProjectEdit() {
      this.$router.push({
        'name': 'project.edit',
        'params': {
          id: this.chooseParams.project
        }
      });
    },
    goFloorEdit() {
      this.$router.push({
        'name': 'floor.edit',
        'params': {
          id: this.chooseParams.floor
        }
      });
    },
    dbclick(row) {
      this.goEdit(row.id);
    },
    handleReset(name) {
      this.$refs[name].resetFields();
    },
    getData(page) {
      let params_ = {
        page: page || this.page.cur + 1,
        pagesize: this.pageSize,
        ids: this.chooseParams.ids
      };
      this.$lodash.assign(params_, this.queryParams);
      this.$apis.resourceIndex(params_).then(res => {
        this.tableData = res.data || [];
        this.page = res.page;
        this.$router.push({
          query: {
            page: this.page.cur
          }
        });
      });
    },
    selectionChange(selection, index) { // 全选选中
      this.selection = selection;
      this.selectedGroup = selection;
    },
    showDel() {
      if (this.selectedGroup.length <= 0) {
        this.$Message.warning('请选择您要删除用户');
        return;
      }


      // this.selectedGroupNames = this.$lodash.map(this.selectedGroup, 'project').join(',');
      this.selectedGroupNames = this.selectedGroup[0].project + '-' + this.selectedGroup[0].floor + '-' + this.selectedGroup[0].door_number;
      this.modelDel = true;
    },
    clearDel() {
      this.$refs['table'].selectAll(false);
      this.selectedGroup = [];
    },
    del() {
      const ids = this.$lodash.map(this.selectedGroup, 'id');
      this.modalLoading = true;
      this.$apis.adminDelete({
        id: ids.join(',')
      }).then(res => {
        this.$lodash.forEach(ids, (curid) => {
          this.$lodash.forEach(this.tableData, (item, index) => {
            if (item && item.id == curid) {
              this.tableData.splice(index, 1);
              --this.page.total;
            }
          });
        });

        if (this.tableData.length + 1 == this.pageSize) {
          this.getData(this.page.cur);
        } else if (this.tableData <= 0 && this.page.cur != 1) {
          this.getData(this.page.cur - 1);
        } else {
          this.getData(1);
        }
        this.modalLoading = false;
        this.modelDel = false;
        this.$Message.success('删除成功');
      });
    }
  }
}

</script>
